{"changed":true,"filter":false,"title":"Council.php","tooltip":"/app/Http/Controllers/Council.php","value":"<?php\nnamespace App\\Http\\Controllers;\nuse App\\Http\\Controllers\\Controller;\n\nuse App\\Council as Model;\nuse App\\Council_group;\nuse App\\Council_detail;\nuse App\\Teacher;\nuse App\\Year;\nuse App\\Intertime_students;\nuse Carbon\\Carbon;\n\nclass Council extends Controller{\n    public function listcouncil($council_group_id = null){\n        if($council_group_id)\n            $council = Model::where('council_group_id',$council_group_id)->get();\n        else\n            $council = Model::all();\n        $council_group = Council_group::all();\n        return view('council.list',['council'=>$council,'council_group'=>$council_group,'council_group_id'=>$council_group_id]);\n    }\n    public function add($intertime_id = null){\n        $teacher = Teacher::all();\n        $council_group = Council_group::all();\n        return view('council.add', ['teacher'=>$teacher,'council_group'=>$council_group]);\n    }\n    public function edit($id){\n        $council = Model::find($id);\n        $teacher = Teacher::all();\n        $council_group = Council_group::all();\n        return view('council.edit',['council'=>$council,'teacher'=>$teacher,'council_group'=>$council_group]);\n    }\n    \n    public function update($id){\n        global $request;\n        $council = Model::find($id);\n        $council->name = $request->get('name');\n        $council->council_group_id = $request->council_group_id;\n        $council->startdate = Carbon::createFromFormat('d/m/Y H:i:s', $request->get('startdate').' '.$request->get('time_start').':00' );\n        $council->enddate = Carbon::createFromFormat('d/m/Y H:i:s', $request->get('startdate').' '.$request->get('time_end').':00' );\n        $council->place = $request->get('place');\n        $council->teacher = json_encode($request->teacher);\n        $council->note = $request->get('note');\n        $council->save();\n        return redirect()->route('council::');\n    }\n   \n    public function create(){\n        global $request;\n        $council = new Model();\n        $council->name = $request->get('name');\n        $council->council_group_id = $request->council_group_id;\n        $council->startdate = Carbon::createFromFormat('d/m/Y H:i:s', $request->get('startdate').' '.$request->get('time_start').':00' );\n        $council->enddate = Carbon::createFromFormat('d/m/Y H:i:s', $request->get('startdate').' '.$request->get('time_end').':00' );\n        $council->place = $request->get('place');\n        $council->teacher = json_encode($request->teacher);\n        $council->note = $request->get('note');\n        $council->save();\n        return redirect()->route('council::');\n    }\n    \n    public function add_council_detail($council_id = null){\n        $council = Model::find($council_id);\n        $students = Intertime_students::where('intertime_id',$council->council_group->intertime_id)->get();\n        //Tat ca sinh vien da duoc them o cac hoi dong truoc\n        $students_selected = Model::where('council_group_id',$council->council_group->id)->get()->first();\n        //Tat ca sinh vien da duoc add trong hoi dong\n        $students_on_selected = $council->council_detail;\n        return view('council_detail.addCouncilDetail' ,['students'=>$students, 'council'=>$council, 'council_id'=>$council_id, 'students_selected'=>$students_selected, 'students_on_selected'=>$students_on_selected]);\n    }\n    public function add_list_council_detail($id){\n        global $request;\n        $ids = $request->ids;\n        if( $ids )\n        foreach ( $ids as $the_id ){\n            $st = new Council_detail();\n            $st->council_id = $id;\n            $st->list_student_by_intershiptime_id = $the_id;\n            $st->save();\n        }\n        return redirect()->route('council::listCouncilDetail',['id'=>$id]);\n    }\n    public function list_council_detail($id){\n        $council = Model::find($id); \n        $students = $council->council_detail;\n        return view('council_detail.listCouncilDetail',['students'=>$students, 'council_id'=>$id]);\n    }\n    public function delete($id){\n        Model::destroy($id);\n        return redirect()->route('council::');\n    }\n    public function view($id){\n        $council = Model::find($id);\n        return view('council.view' ,['council'=>$council]);\n    }\n}\n\n?>","undoManager":{"mark":100,"position":100,"stack":[[{"start":{"row":84,"column":19},"end":{"row":84,"column":20},"action":"insert","lines":["s"],"id":686}],[{"start":{"row":84,"column":21},"end":{"row":84,"column":22},"action":"insert","lines":[";"],"id":687}],[{"start":{"row":74,"column":22},"end":{"row":74,"column":40},"action":"remove","lines":["Intertime_students"],"id":688},{"start":{"row":74,"column":22},"end":{"row":74,"column":23},"action":"insert","lines":["C"]}],[{"start":{"row":74,"column":23},"end":{"row":74,"column":24},"action":"insert","lines":["o"],"id":689}],[{"start":{"row":74,"column":24},"end":{"row":74,"column":25},"action":"insert","lines":["u"],"id":690}],[{"start":{"row":74,"column":25},"end":{"row":74,"column":26},"action":"insert","lines":["n"],"id":691}],[{"start":{"row":74,"column":26},"end":{"row":74,"column":27},"action":"insert","lines":["c"],"id":692}],[{"start":{"row":74,"column":27},"end":{"row":74,"column":28},"action":"insert","lines":["i"],"id":693}],[{"start":{"row":74,"column":28},"end":{"row":74,"column":29},"action":"insert","lines":["l"],"id":694}],[{"start":{"row":74,"column":29},"end":{"row":74,"column":30},"action":"insert","lines":["-"],"id":695}],[{"start":{"row":74,"column":30},"end":{"row":74,"column":31},"action":"insert","lines":["d"],"id":696}],[{"start":{"row":74,"column":31},"end":{"row":74,"column":32},"action":"insert","lines":["e"],"id":697}],[{"start":{"row":74,"column":32},"end":{"row":74,"column":33},"action":"insert","lines":["t"],"id":698}],[{"start":{"row":74,"column":33},"end