{"changed":true,"filter":false,"title":"division.php","tooltip":"/app/Http/Controllers/division.php","value":"<?php\nnamespace App\\Http\\Controllers;\nuse App\\Http\\Controllers\\Controller;\nuse App\\Division as Model;\nuse App\\department;\n\nclass Division extends Controller{\n    public function listdivision(){\n        $division = Model::all();\n        return view('division.list',['division'=>$division]);\n    }\n    public function add(){\n        return view('division.add');\n    }\n    public function edit($id){\n        $division = Model::find($id);\n        return view('division.edit',['division'=>$division]);\n    }\n    public function update($id){\n        global $request;\n        $division = Model::find($id);\n        $division->name_course = $request->get('name_course');\n        $division->type_learn = $request->get('type_learn');\n        $division->name_teachers = $request->get('name_teachers');\n        $division->name_subject = $request->get('name_subject');\n        $division->formal = $request->get('formal');\n        $division->lanhoc = $request->get('lanhoc');\n        $division->note = $request->get('note');\n        $division->save();\n        return redirect()->route('division::');\n    }\n    public function create(){\n        global $request;\n        $division = new Model();\n        $division->name_course = $request->get('name_course');\n        $division->type_learn = $request->get('type_learn');\n        $division->name_teachers = $request->get('name_teachers');\n        $division->name_subject = $request->get('name_subject');\n        $division->formal = $request->get('formal');\n        $division->lanhoc = $request->get('lanhoc');\n        \n        $division->save();\n        return redirect()->route('division::');\n    }\n    public function delete($id){\n        Model::destroy($id);\n        return redirect()->route('division::');\n    }\n}\n\n?>","undoManager":{"mark":-2,"position":3,"stack":[[{"start":{"row":6,"column":6},"